import React from 'react';
import '../Style/Home.css';
import { useUser } from '../UserContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Home = () => {
  const { isAuthenticated, setIsAuthenticated } = useUser();
  const navigate = useNavigate();

  // Function to handle logout
  const handleLogout = async () => {
    try {
      // Make a POST request to the backend logout route
      await axios.post('http://localhost:8000/api/logout');

      // Clear any user-related data or authentication tokens from the frontend
      // For example, if using a context or local storage, clear the authentication state
      setIsAuthenticated(false);

      // Redirect to the login page after logout
      navigate('/login');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {isAuthenticated ? (
        <>
          <p className='home-txt'>Hello World</p>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <p>Please login to access the home page.</p>
      )}
    </div>
  );
};

export default Home;
