import React from 'react';
import '../Style/Home.css';
import { useUser } from '../UserContext';

const Home = () => {
  const { isAuthenticated } = useUser();

  // Function to handle logout
  const handleLogout = () => {
    // Implement logout logic here, e.g., clearing the authentication token, etc.
    // After logging out, you should redirect the user to the login page.
  };

  return (
    <div>
      {isAuthenticated ? (
        <>
          <p className='home-txt'>Hello World</p>
          <button onClick={handleLogout}>Logout</button>
        </>
      ) : (
        <p>Please login to access the home page.</p>
      )}
    </div>
  );
};

export default Home;
