import React from 'react';
import './App.css';
import { Route, Routes } from 'react-router-dom';
import Error from './Components/Error';
import Home from './Components/Home';
import Login from './Components/Login';
import Signup from './Components/Signup';
import ProtectedRoute from './ProtectedRoute'; // Import the ProtectedRoute component

function App() {
  // Assume you have some state or logic to track whether the user is authenticated
  // You can implement authentication using state, local storage, or a context API, for example.
  const isAuthenticated = false; // Change this to true/false based on the user's authentication status

  return (
    <div className="App">
      <Routes>
        <ProtectedRoute path="/" element={<Home />} isAuthenticated={isAuthenticated} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="*" element={<Error />} />
      </Routes>
    </div>
  );
}

export default App;
